import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import { Input, Badge, Button } from "@chakra-ui/react";
import { FormControl, FormLabel } from "@chakra-ui/react";
import { Card, CardHeader, CardBody } from "@chakra-ui/react";
import "./Pages.style.css";
import {
  List,
  ListItem,
  ListIcon,
  OrderedList,
  UnorderedList,
} from "@chakra-ui/react";

const Dashboard = () => {
  const navigate = useNavigate();

  const [quotes, setQuotes] = useState([]);

  const [tempQuote, setTempQuote] = useState("");

  async function populateQuote() {
    const data = await fetch("http://localhost:1337/api/quote", {
      method: "GET",
      headers: {
        "x-access-token": localStorage.getItem("token"),
      },
    });

    const response = await data.json();

    if (response.status === "ok") {
      setQuotes(response.quotes);
    } else {
      alert(response.error);
    }
  }

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      const user = jwtDecode(token);

      if (!user) {
        navigate("/login");
        localStorage.removeItem("token");
      } else {
        populateQuote();
      }
    } else {
      navigate("/login");
    }
  }, []);

  async function updateQuote(event) {
    event.preventDefault();
    const data = await fetch("http://localhost:1337/api/quote", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "x-access-token": localStorage.getItem("token"),
      },
      body: JSON.stringify({
        quote: tempQuote,
      }),
    });

    const response = await data.json();
    console.log(response.status);
    if (response.status === "ok") {
      setTempQuote("");
      setQuotes([...quotes, tempQuote]); // Update the quotes array
    } else {
      alert(response.error);
    }
  }

  function handleLogout() {
    localStorage.removeItem("token");
    navigate("/login");
  }

  return (
    <div>
      <Card>
        <CardHeader>
          <div className="header">
            <h1>Create Quote</h1>
            <Button onClick={handleLogout}>Logout</Button>
          </div>
        </CardHeader>
        <CardBody>
          <form onSubmit={updateQuote}>
            <FormControl>
              <FormLabel>Quote</FormLabel>
              <Input
                type="text"
                name="quote"
                value={tempQuote}
                onChange={(e) => setTempQuote(e.target.value)}
                id="quote"
                placeholder="Enter your quote"
              />
              <Input
                mt={4}
                cursor={"pointer"}
                type="submit"
                value="Get Quote"
              />
            </FormControl>
          </form>
        </CardBody>
      </Card>

      <Card mt={4}>
        <CardHeader>Your Quote:</CardHeader>
        <CardBody>
          <Badge colorScheme="teal" padding={2} width={"100%"}>
            <UnorderedList>
              {quotes.map((quote, index) => (
                <ListItem>
                  <Badge
                    key={index}
                    colorScheme="teal"
                    padding={2}
                    width={"100%"}
                  >
                    {quote}
                  </Badge>
                </ListItem>
              ))}
            </UnorderedList>
            {quotes.length === 0 && <p>No quotes found</p>}
          </Badge>
        </CardBody>
      </Card>
    </div>
  );
};

export default Dashboard;
