import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import { FormControl, FormLabel, Input, Badge, Button } from "@chakra-ui/react";
import {
  Card,
  CardHeader,
  CardBody,
  Grid,
  GridItem,
  Box,
  ListItem,
  OrderedList,
  Image,
} from "@chakra-ui/react";
import "./Pages.style.css";

import { DeleteIcon, EditIcon } from "@chakra-ui/icons";

const Dashboard = () => {
  const navigate = useNavigate();

  const [quotes, setQuotes] = useState([]);

  const [tempQuote, setTempQuote] = useState("");

  async function populateQuote() {
    const data = await fetch("http://localhost:1337/api/quote", {
      method: "GET",
      headers: {
        "x-access-token": localStorage.getItem("token"),
      },
    });

    const response = await data.json();

    if (response.status === "ok") {
      setQuotes(response.quotes);
    } else {
      alert(response.error);
    }
  }

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      const user = jwtDecode(token);

      if (!user) {
        navigate("/login");
        localStorage.removeItem("token");
      } else {
        populateQuote();
      }
    } else {
      navigate("/login");
    }
  }, []);

  async function createQuote(event) {
    event.preventDefault();

    if (tempQuote === "") {
      alert("Please enter a quote");
      return;
    } else {
      const data = await fetch("http://localhost:1337/api/quote", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "x-access-token": localStorage.getItem("token"),
        },
        body: JSON.stringify({
          quote: tempQuote,
        }),
      });

      const response = await data.json();
      console.log(response.status);
      if (response.status === "ok") {
        setTempQuote("");
        setQuotes([...quotes, tempQuote]); // Update the quotes array
      } else {
        alert(response.error);
      }
    }
  }

  async function editQuote(index) {
    const currentQuote = quotes[index];
    const newQuote = prompt("Enter new quote", currentQuote);

    if (newQuote !== null && newQuote.trim() !== "") {
      const updatedQuotes = [...quotes];
      updatedQuotes[index] = newQuote;

      // Make API request to update the quotes on the server
      const data = await fetch("http://localhost:1337/api/quote", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          "x-access-token": localStorage.getItem("token"),
        },
        body: JSON.stringify({
          quotes: updatedQuotes,
        }),
      });

      const response = await data.json();
      if (response.status === "ok") {
        setQuotes(updatedQuotes);
      } else {
        alert(response.error);
      }
    } else if (newQuote !== null) {
      // Show an alert if the user entered an empty quote
      alert("Please enter a non-empty quote.");
    }
  }

  async function deleteQuote(index) {
    const updatedQuotes = [...quotes];
    updatedQuotes.splice(index, 1);

    // Make API request to update the quotes on the server
    const data = await fetch("http://localhost:1337/api/quote", {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "x-access-token": localStorage.getItem("token"),
      },
      body: JSON.stringify({
        quotes: updatedQuotes,
      }),
    });

    const response = await data.json();
    if (response.status === "ok") {
      setQuotes(updatedQuotes);
    } else {
      alert(response.error);
    }
  }

  function handleLogout() {
    localStorage.removeItem("token");
    navigate("/login");
  }

  const property = {
    imageUrl: " ../../client/public/images/quotes-bg.jpg",
    imageAlt: quotes,
    imageWidth: "100%",
    title: quotes,
  };

  return (
    <div>
      <Card>
        <CardHeader>
          <div className="header">
            <h1>Create Quote</h1>
            <Button onClick={handleLogout}>Logout</Button>
          </div>
        </CardHeader>
        <CardBody>
          <form onSubmit={createQuote}>
            <FormControl>
              <FormLabel>Quote</FormLabel>
              <Input
                type="text"
                name="quote"
                value={tempQuote}
                onChange={(e) => setTempQuote(e.target.value)}
                id="quote"
                placeholder="Enter your quote"
              />
              <Input
                mt={4}
                cursor={"pointer"}
                type="submit"
                value="Get Quote"
              />
            </FormControl>
          </form>
        </CardBody>
      </Card>

      <Card mt={4}>
        <CardHeader>Your Quotes:</CardHeader>
        <CardBody>
          <Grid templateColumns="repeat(5, 1fr)" gap={6}>
            <GridItem w="100%" h="10" bg="blue.500">
              <Box
                maxW="sm"
                borderWidth="1px"
                borderRadius="lg"
                overflow="hidden"
              >
                <Image src={property.imageUrl} alt={property.imageAlt} />

                <Box p="6">
                  <Box
                    mt="1"
                    fontWeight="semibold"
                    as="h4"
                    lineHeight="tight"
                    noOfLines={1}
                  >
                    {property.title}
                  </Box>
                </Box>
              </Box>
            </GridItem>
          </Grid>
          <OrderedList>
            {quotes.map((quote, index) => (
              <ListItem className="list" key={index}>
                <Badge
                  key={index}
                  variant="outline"
                  colorScheme="green"
                  padding={2}
                  width={"100%"}
                  borderRadius="md"
                  textAlign="center"
                  fontSize="lg"
                  fontWeight="bold"
                  textTransform="uppercase"
                  mb={2}
                >
                  {quote}
                </Badge>
                <div className="btn-container">
                  <Button
                    ml={2}
                    size="sm"
                    variant="ghost"
                    colorScheme="red"
                    onClick={() => editQuote(index)}
                  >
                    <EditIcon w={4} h={4} color="red.500" />
                  </Button>
                  <Button
                    ml={2}
                    size="sm"
                    variant="ghost"
                    colorScheme="red"
                    onClick={() => deleteQuote(index)}
                  >
                    <DeleteIcon w={4} h={4} color="red.500" />
                  </Button>
                </div>
              </ListItem>
            ))}
          </OrderedList>
          {quotes.length === 0 && <p>No quotes found</p>}
        </CardBody>
      </Card>
    </div>
  );
};

export default Dashboard;
